'use strict';

////////////////////////////////////////////////////////////////////////////////
// require
////////////////////////////////////////////////////////////////////////////////

const Singleton = require('./singleton.js');


////////////////////////////////////////////////////////////////////////////////
// Global
////////////////////////////////////////////////////////////////////////////////

class Global extends Singleton {
  /**
   * 唯一のGlobalインスタンス
   * @constructor
   * @example
const globalObject = require('./global').GlobalObject;
globalObject.sampleValue = 1;
   */
  constructor(){
    super(...arguments, 'GlobalObjects', Global);
  }
}

////////////////////////////////////////////////////////////////////////////////
// EXPORTS
////////////////////////////////////////////////////////////////////////////////

module.exports = new Global();

